import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const FormWrapper = styled.div`
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 5px;
  background-color: #bf80ff;
  max-width: 600px;
  margin: 0 auto;
`;

const FormField = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Select = styled.select`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background: #007bff;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background: #0056b3;
  }
`;

const CircularButton = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: 1px solid #ccc;
  background-color: #f1f1f1;
  cursor: pointer;
  margin: auto;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ErrorMessage = styled.div`
  color: red;
  margin-top: 10px;
`;

const CurrencySwapForm = ({ currencies }) => {
    const [amount, setAmount] = useState('');
    const [buyAmount, setBuyAmount] = useState('');
    const [fromCurr, setFromCurr] = useState('');
    const [toCurr, setToCurr] = useState('');
    const [exchangeRate, setExchangeRate] = useState(null);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Calculate exchange rate whenever fromCurr or toCurr changes
        if (fromCurr && toCurr) {
            const rate = calcExchangeRate(fromCurr, toCurr);
            setExchangeRate(rate);
        }
    }, [fromCurr, toCurr, currencies]);

    const calcExchangeRate = (from, to) => {
        const fromPrice = currencies.find(item => item.currency === from)?.price;
        const toPrice = currencies.find(item => item.currency === to)?.price;

        if (fromPrice && toPrice) {
            return toPrice / fromPrice;
        }

        return null;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!fromCurr || !toCurr) {
            setError('Please fill in all fields');
            return;
        }

        setError('');
        const rate = calcExchangeRate(fromCurr, toCurr);
        if (rate) {
            const converted = amount * rate;
            const display = `Converted ${amount} ${fromCurr} to ${converted.toFixed(4)} ${toCurr}`;
            setMessage(display);
        } else {
            setMessage('');
        }
    };

    const handleFlip = () => {
        const temp = fromCurr;
        setFromCurr(toCurr);
        setToCurr(temp);
    }

    const handleAmountChange = (value) => {
        setAmount(value);
        if (fromCurr && toCurr) {
            const rate = calcExchangeRate(fromCurr, toCurr);
            const converted = value * rate;
            setBuyAmount(converted.toFixed(4));
        }
    };

    const handleBuyAmountChange = (value) => {
        setBuyAmount(value);
        if (fromCurr && toCurr) {
            const rate = calcExchangeRate(toCurr, fromCurr);
            const converted = value * rate;
            setAmount(converted.toFixed(4));
        }
    };

    return (
        <FormWrapper>
            <form onSubmit={handleSubmit}>
                <FormField>
                    <Label style={{ fontWeight: 'bold' }}>To Sell</Label>
                    <Label style={{ fontWeight: 'bold' }}>Amount</Label>
                    <Input
                        type="number"
                        value={amount}
                        onChange={(e) => handleAmountChange(e.target.value)}
                        style={{ width: '100px' }}
                        placeholder='Amount'
                        required
                    />
                    <Select
                        value={fromCurr}
                        onChange={(e) => setFromCurr(e.target.value)}
                        required
                    >
                        <option value="">Select a Currency</option>
                        {currencies.map((crypto) => (
                            <option key={crypto.currency} value={crypto.currency}>
                                {crypto.currency}
                            </option>
                        ))}
                    </Select>
                </FormField>
                <CircularButton type="button" onClick={handleFlip}>
                    â†• Flip
                </CircularButton>
                <FormField>
                    <Label style={{ fontWeight: 'bold' }}>To Buy</Label>
                    <Input
                        type="number"
                        value={buyAmount}
                        onChange={(e) => handleBuyAmountChange(e.target.value)}
                        style={{ width: '100px' }}
                        placeholder='Amount'
                        required
                    />
                    <Select
                        value={toCurr}
                        onChange={(e) => setToCurr(e.target.value)}
                        required
                    >
                        <option value="">Select a Currency</option>
                        {currencies.map((crypto) => (
                            <option key={crypto.currency} value={crypto.currency}>
                                {crypto.currency}
                            </option>
                        ))}
                    </Select>
                </FormField>

                {exchangeRate && (
                    <p>Exchange Rate: 1 {fromCurr} = {exchangeRate.toFixed(4)} {toCurr}</p>
                )}
                <Button type="submit">Swap currency</Button>
                {message && (
                    <p style={{ textAlign: 'center', backgroundColor: 'purple', padding: '10px', borderRadius: '5px', fontWeight: 'bold' , color: 'white'}}>
                        {message}
                    </p>
                )}
                {error && <ErrorMessage>{error}</ErrorMessage>}
            </form>
        </FormWrapper>
    )
}

export default CurrencySwapForm;
