import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const FormWrapper = styled.div`
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 5px;
  background: #f9f9f9;
  max-width: 600px;
  margin: 0 auto;
`;

const FormField = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Select = styled.select`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background: #007bff;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background: #0056b3;
  }
`;

const ErrorMessage = styled.div`
  color: red;
  margin-top: 10px;
`;

const CurrencySwapForm = ({ currencies }) => {
    const [amount, setAmount] = useState('0');
    const [fromCurr, setFromCurr] = useState('');
    const [toCurr, setToCurr] = useState('');
    const [exchangeRate, setExchangeRate] = useState(null);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');
    const [convertedAmount, setConvertedAmount] = useState(null);

    const calcExchangeRate = (from, to) => {
        const fromPrice = currencies.find(item => item.currency === from)?.price;
        const toPrice = currencies.find(item => item.price === to)?.price;

        if (fromPrice && toPrice) {
            return toPrice / fromPrice;
        }

        return null;
    }

    useEffect(() => {
        if (fromCurr && toCurr) {
            const rate = calcExchangeRate(fromCurr, toCurr);
            setExchangeRate(rate);
        }
    }, [fromCurr, toCurr, currencies]);

    const handleSwap = () => {
        if (exchangeRate && amount) {
            const converted = amount * exchangeRate;
            setConvertedAmount(converted);
            const display = `Converted ${amount} ${fromCurr} to ${converted.toFixed(4)} ${toCurr}`;
            setMessage(display);
            console.log(display);
        }
    }


    const handleSubmit = (e) => {
        e.preventDefault();
        if (amount === 0 || !fromCurr || !toCurr) {
            setError('Please fill in all fields');
        }

        //swap curr
        alert(`Swapped ${amount} ${fromCurr} to ${toCurr}`);

        handleSwap();
    }

    return (
        <FormWrapper>
            <form onSubmit={handleSubmit}>
                <FormField>
                    <Label>To Sell</Label>
                    <Select
                        value={fromCurr}
                        required
                        onChange={(e) => setFromCurr(e.target.value)}
                    >
                        <option value="">Select a Currency</option>
                        {currencies.map((crypto) => (
                            <option key={crypto.currency} value={crypto.currency}>
                                {crypto.currency}
                            </option>
                        ))}
                    </Select>
                </FormField>
                <FormField>
                    <Label>Amount</Label>
                    <Input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        required />
                </FormField>
                <FormField>
                    <Label>To Buy</Label>
                    <Select
                        value={toCurr}
                        required
                        onChange={(e) => setToCurr(e.target.value)}
                    >
                        <option value="">Select a Currency</option>
                        {currencies.map((crypto) => (
                            <option key={crypto.currency} value={crypto.currency}>
                                {crypto.currency}
                            </option>
                        ))}
                    </Select>
                </FormField>

                {exchangeRate && (
                    <p>Exchange Rate: 1 {fromCurr} = {exchangeRate.toFixed(4)} {toCurr}</p>
                )}
                <Button
                    type="submit"
                    onClick={handleSwap}>
                    Swap currency
                </Button>
                {message && (
                    <p>{message}</p>
                )}
                {error && <ErrorMessage>{error}</ErrorMessage>}
            </form>
        </FormWrapper>
    )
}

export default CurrencySwapForm;
